    public static void main(String[] args) {

        Scanner in = new Scanner(System.in);
        String[] dataStr = (in.nextLine()).split(" ");
        int charRepNum = 10;
        for (char i = 'a'; i <= 'z'; i++) {
            map.put(i, charRepNum++);
        }
        for (int i = 0; i < 9; i++) {
            map.put((char) (i + 48), i);
        }


        int givenRadixNumPosi = Integer.parseInt(dataStr[2]);
